-- Se crean las tablas fisicas correspondientes a los estamentos
CREATE TABLE COMUN.dbo.estamento(
	esta_codigo INT PRIMARY KEY,
	esta_nombre VARCHAR(50),
	usuario_mod VARCHAR(50),
	fecha_mod DATETIME
);
