INSERT INTO COMUN.dbo.estamento VALUES (1, 'ADMINISTRATIVAS', 'MMONTALBA', GETDATE());

CREATE TABLE COMUN.dbo.cargo_estamento(
	FUNC_COD_CCOSTO INT,
	FUNC_COD_CARGO INT,
	esta_codigo INT,
	usuario_mod VARCHAR(50),
	fecha_mod DATETIME,
	PRIMARY KEY(FUNC_COD_CCOSTO, FUNC_COD_CARGO),
	FOREIGN KEY(esta_codigo) REFERENCES COMUN.dbo.estamentos(esta_codigo)
);

-- Se insertan todos los cargos con los estamentos identificados
WITH estamentos_insersion AS (
SELECT DISTINCT a.FUNC_COD_CCOSTO,
				a.FUNC_COD_CARGO,
				b.estamento
FROM elyon.COMUN.dbo.funcionarios a
INNER JOIN @funcionarios_estamento b ON RTRIM(LTRIM(a.FUNC_RUT))=RTRIM(LTRIM(b.rut_completo))
)

INSERT INTO COMUN.dbo.cargo_estamento
SELECT FUNC_COD_CCOSTO, FUNC_COD_CARGO,
	   CASE WHEN estamento='ADMINISTRATIVAS' THEN 1 ELSE 0 END,
	   'MMONTALBA', GETDATE()
FROM estamentos_insersion
ORDER BY FUNC_COD_CCOSTO,
		 FUNC_COD_CARGO,
		 estamento ASC;

-- Tabla final que se utiliza en PowerBI
SELECT a.*, 
	   CASE WHEN b.esta_codigo IS NULL THEN 'SIN IDENTIFICAR' ELSE c.esta_nombre END FUNC_NOM_ESTAMENTO
FROM (
	SELECT a.FUNC_NFICHA
		  ,a.FUNC_RUT
		  ,a.FUNC_RUT_NRO
		  ,a.FUNC_RUT_DV 
		  ,CASE WHEN a.FUNC_ESTADO='A' THEN 'ACTIVO/A' ELSE 'SUSPENDIDO/A' END FUNC_ESTADO
		  ,a.FUNC_NOMBRES
		  ,CASE WHEN a.FUNC_SEXO='M' THEN 'MASCULINO'
				WHEN a.FUNC_SEXO='F' THEN 'FEMENINO'
		   ELSE 'SIN IDENTIFICAR' END FUNC_SEXO
		  ,a.FUNC_FECHA_NAC
		  ,a.FUNC_ANHO_INGRESO
		  ,LOWER(RTRIM(LTRIM(a.FUNC_EMAIL))) FUNC_EMAIL
		  ,CASE WHEN a.FUNC_EST_CIVIL='' THEN 'SIN IDENTIFICAR' ELSE a.FUNC_EST_CIVIL END FUNC_EST_CIVIL
		  ,a.FUNC_NACIONALIDAD
		  ,a.FUNC_COD_CCOSTO
		  ,CASE WHEN a.FUNC_COD_CCOSTO=0 THEN 'SIN IDENTIFICAR' ELSE a.FUNC_NOM_CCOSTO END FUNC_NOM_CCOSTO
		  ,a.FUNC_COD_CATEGORIA
		  ,CASE WHEN a.FUNC_COD_CATEGORIA=0 THEN 'SIN IDENTIFICAR' ELSE a.FUNC_NOM_CATEGORIA END FUNC_NOM_CATEGORIA
		  ,a.FUNC_COD_UNIDAD
		  ,CASE WHEN a.FUNC_COD_UNIDAD=0 THEN 'SIN IDENTIFICAR' ELSE a.FUNC_NOM_UNIDAD END FUNC_NOM_UNIDAD
		  ,a.FUNC_COD_GRADO
		  ,CASE WHEN a.FUNC_COD_GRADO=0 THEN 'SIN IDENTIFICAR' ELSE a.FUNC_NOM_GRADO END FUNC_NOM_GRADO
		  ,a.FUNC_COD_CARGO
		  ,CASE WHEN a.FUNC_COD_CARGO=-0 THEN 'SIN IDENTIFICAR' ELSE a.FUNC_DESC_CARGO END FUNC_DESC_CARGO 
		  ,a.FUNC_COD_TITULO
		  ,a.FUNC_NOM_TITULO
		  ,a.FUNC_COD_PLANTA
		  ,a.FUNC_NOM_PLANTA
		  ,a.FUNC_FECHA_ING
		  ,a.FUNC_FECHA_RET
	FROM (
		SELECT *, ROW_NUMBER() OVER (PARTITION BY FUNC_EMAIL ORDER BY FUNC_FECHA_ING DESC) orden_ingreso
		FROM elyon.COMUN.dbo.funcionarios
	) a 
	WHERE a.orden_ingreso=1
) a
LEFT JOIN COMUN.dbo.cargo_estamento b ON a.FUNC_COD_CCOSTO=b.FUNC_COD_CCOSTO AND a.FUNC_COD_CARGO=b.FUNC_COD_CARGO
LEFT JOIN COMUN.dbo.estamento c ON b.esta_codigo=c.esta_codigo;